}

// Section one----------------
.sectionOne {
  height: 100vh;
  display: grid;
  grid-template-columns: 45% 55%;
  letter-spacing: 0.1rem;
  .sectionOne__content {
    color: #000000d2;
    nav {
      padding: 3% 5% 0 5%;
      text-transform: capitalize;
      ul {
        list-style-type: none;
        margin: 0;
        padding: 0;
        li {
          display: inline;
