    background-color: rgba(53, 53, 53, 0.48);
    height: 8rem;
    width: 60%;

    display: grid;
    grid-template-columns: repeat(4, 1fr);
    .Text {
      display: flex;
      flex-direction: column;
      justify-content: center;
      align-items: center;

      font-weight: bold;
      font-size: 1.3rem;
    }
  }
}

// about ----------------------

.about {
  padding: 0 5% 0 5%;
  height: 65vh;
  display: grid;
  grid-template-columns: repeat(4, 1fr);
  grid-gap: 1.5rem;
  // background-color: #2e2e2e/21;

  .card {
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    line-height: 1.8rem;
    padding: 0.5rem;
    text-align: center;
    border-right: solid rgba(0, 0, 0, 0.144) 1px;
    .inner {
      display: flex;
      flex-direction: column;
      justify-content: center;
      align-items: center;
      font-size: 4.5rem;
      height: 5rem;
      width: 5rem;
      border: solid rgba(0, 0, 0, 0.719) 3px;
      padding: 1.5rem;
      border-radius: 50%;

